$(document).ready(function(){$("#sidebar").focusin(function(a){$("#sidebar").toggleClass("child-focus")}).focusout(function(a){$("#sidebar").toggleClass("child-focus")});$("#sidebar nav ul li.has-children > a").click(function(a){a.preventDefault();var b=$(this).parent("li");if(b.hasClass("active")){$(this).next().slideUp("fast",function(){b.removeClass("active")})}else{b.addClass("active");$(this).next("ul").css("display","none").slideDown("fast");b.siblings("li.has-children").find("ul").slideUp("fast",function(){$(this).parent("li").removeClass("active")})}})});